$args = [
  "base" => str_replace(999999999, "%#%", esc_url(get_pagenum_link(999999999))),
  "format" => "?paged=%#%",
  "current" => max(1, $paged),
  "total" => $max,
  "mid_size" => 2,
  "end_size" => 1,
  "prev_text" =>
    '<svg role="img" aria-label="前のページ" width="8" height="14" viewBox="0 0 8 14" fill="none"><path d="M7 1L1 7L7 13" stroke="currentColor" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/></svg>',
