const okLchToSrgb = ({l, c, h}) => {
  const L = l / 100;
  const a = Math.cos((h * Math.PI) / 180) * c;
  const b = Math.sin((h * Math.PI) / 180) * c;

  const lVal = L + 0.3963377774 * a + 0.2158037573 * b;
  const mVal = L - 0.1055613458 * a - 0.0638541728 * b;
  const sVal = L - 0.0894841775 * a - 1.2914855480 * b;

  const lCubed = lVal ** 3;
  const mCubed = mVal ** 3;
  const sCubed = sVal ** 3;

  let R = 4.0767416621 * lCubed - 3.3077115913 * mCubed + 0.2309699292 * sCubed;
  let G = -1.2684380046 * lCubed + 2.6097574011 * mCubed - 0.3413193965 * sCubed;
  let B = -0.0041960863 * lCubed - 0.7034186147 * mCubed + 1.7076147010 * sCubed;

  const srgb = [R, G, B].map((channel) => {
    if (channel <= 0.0031308) {
      return channel * 12.92;
    }
    return 1.055 * Math.pow(channel, 1 / 2.4) - 0.055;
  });

  const [r, g, bVal] = srgb;

  return `#${toHex(r)}${toHex(g)}${toHex(bVal)}`.toUpperCase();
};

